from urllib.request import urlopen

def main():
    result = urlopen('https://www.cryptonator.com/api/ticker/gbp-doge').read()

    if result['ticker']['change'] > '0':
        print((' Ð%.0f |  color=#000000' %
              float(result['ticker']['price'])))
    else:
        print((' Ð%.0f |  color=#000000' %
              float(result['ticker']['price'])))


if __name__ == '__main__':
    main()
