#!/bin/sh

# <xbar.title>Colima Status</xbar.title>
# <xbar.version>1.0</xbar.version>
# <xbar.desc>Displays Colima Status - https://github.com/abiosoft/colima</xbar.desc>
# <xbar.image>https://raw.githubusercontent.com/qoomon/xbar-colima-status/main/screenshot.png</xbar.image>
# <xbar.abouturl>https://github.com/qoomon/xbar-colima-status/</xbar.abouturl>
# <xbar.dependencies>colima</xbar.dependencies>
# <xbar.author>Bengt Brodersen</xbar.author>
# <xbar.author.github>qoomon</xbar.author.github>

PATH="/opt/homebrew/bin:/usr/local/bin:$PATH"

COLIMA_RUNNING_ICON=''
COLIMA_PROGRESS_ICON=''
COLIMA_STOPPED_ICON=''

COLIMA_STATUS_FILE='/tmp/xbar_colima_status'

colima_status="$([ -e "$COLIMA_STATUS_FILE" ] && cat "$COLIMA_STATUS_FILE")"

if [ $# = 0 ]
then
    if [ "$colima_status" = starting ]
    then
        echo "|templateImage=$COLIMA_PROGRESS_ICON"
    	echo '---'
    	echo "Starting Colima..."
        exit 0
    fi

    if [ "$colima_status" = stopping ]
    then
        echo "|templateImage=$COLIMA_PROGRESS_ICON"
    	echo '---'
    	echo "Stopping Colima..."
    	exit 0
    fi

    if  (colima status >/dev/null)
    then
    	echo "|templateImage=$COLIMA_RUNNING_ICON"
    	echo '---'
    	echo "Stop Colima | terminal=false bash=$0 param1=stop"
    else
        echo "| templateImage=$COLIMA_STOPPED_ICON"
    	echo '---'
    	echo "Start Colima | terminal=false bash=$0 param1=start"
    fi

    exit 0
fi


if [  $1 = start ]
then
    echo "starting" > $COLIMA_STATUS_FILE
    colima start
    echo "" > $COLIMA_STATUS_FILE
    exit 0
fi

if [  $1 = stop ]
then
    echo "stopping" > $COLIMA_STATUS_FILE
    colima stop
    echo "" > $COLIMA_STATUS_FILE
    exit 0
fi

exit 1
