#!/bin/bash

# <xbar.title>Journaling with The Archive</xbar.title>
# <xbar.version>v1.0</xbar.version>
# <xbar.author>Bruno Conte</xbar.author>
# <xbar.author.github>brunocbr</xbar.author.github>
# <xbar.desc>Activates The Archive and remind for Journaling</xbar.desc>
# <xbar.image>https://forum.zettelkasten.de/uploads/editor/xm/regzdy1vfk24.gif</xbar.image>
# <xbar.dependencies>bash,applescript,The Archive</xbar.dependencies>
# <xbar.abouturl>https://forum.zettelkasten.de/discussion/1559/better-journaling-with-the-archive-and-bitbar</xbar.abouturl>

SCRIPT_NAME=$(basename "$0")
SCRIPT_DIR=$(dirname "$0")
FLAG_FILE="$TMPDIR/remember-journal.tmp"
MENU_LINE="| bash='${SCRIPT_DIR}/${SCRIPT_NAME}' param1=click terminal=false"
DAY=`date +%Y%m%d`
ALERT_MODE=0

# Bookmark icon by Kiranshastry
# https://pngtree.com/so/bookmark-icon
ICON=""
ICON_ALERT=""
INTERFACE_STYLE=$(defaults read -g AppleInterfaceStyle 2>/dev/null)
if [[ "$INTERFACE_STYLE" = "Dark" ]]; then
    ICON=""
fi
CUR_ICON=${ICON}


if [[ -f "${FLAG_FILE}" ]]; then
    CUR_ICON=${ICON_ALERT}
    ALERT_MODE=1
fi

function switchTheArchive() {
    if [[ "${ALERT_MODE}" = 1 ]] ; then
        open "thearchive://match/${DAY}"
    else
        osascript <<EOF
tell application "System Events"
    set activeApp to name of application processes whose frontmost is true
    if (activeApp begins with "The Archive") then
        set visible of application process "The Archive" to false
    else
        do shell script "open \"thearchive://match/${DAY}\""
    end if
end tell
EOF
    fi
}

if [[ "$1" = "click" ]]; then
    switchTheArchive
    if [ -f "${FLAG_FILE}" ] ; then
        rm -f "${FLAG_FILE}"
        open 'xbar://refreshPlugin?name=journal.sh'
    fi
fi

echo "$MENU_LINE image=${CUR_ICON}"
